// =========================
// Backend
// =========================
const BACKEND_URL = "http://127.0.0.1:5000";

// =========================
// State
// =========================
let activeRank = "Deck Rating (AB/OS)";
let candidate = { name: "", email: "" };
let quiz = [];               // 60 questions picked for this attempt
let idx = 0;
let selections = [];         // selected index per question
let locked = [];             // lock after attempt
let startedAt = null;        // timestamp (ms) when first answer selected
let timerInterval = null;
const QUIZ_SECONDS = 60 * 60;  // 1 hour

function validateStartForm() {
    const name = candidateName.value.trim();
    const email = candidateEmail.value.trim();
    const emailOk = /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
    if (!name) return "Enter candidate name.";
    if (!emailOk) return "Enter a valid email address.";
    return null;
}

async function startQuiz() {
    const err = validateStartForm();
    if (err) {
        startHint.textContent = err;
        return;
    }

    const name  = candidateName.value.trim();
    const email = candidateEmail.value.trim();

    // --- Credential check ---
    startBtn.disabled = true;
    startHint.textContent = "Verifying credentialsâ¦";

    let credOk = false;
    try {
        const resp = await fetch(`${BACKEND_URL}/check_credentials`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ username: name, email: email })
        });
        const data = await resp.json();

        if (data.ok) {
            credOk = true;
            startHint.textContent = "Fill name + email, then start.";
        } else {
            // Show the exact server message (e.g. username mismatch)
            startHint.textContent = data.message || "Credential check failed. Please try again.";
        }
    } catch (e) {
        // Network / server unreachable â block the quiz
        startHint.textContent = "Cannot reach the server. Please check your connection.";
    } finally {
        startBtn.disabled = false;
    }

    if (!credOk) return;

    // --- Proceed to quiz ---
    activeRank = rankSelect.value;
    candidate = { name, email };

    // Pick random 60
    const bank = BANKS[activeRank] || [];
    quiz = shuffle(bank).slice(0, 60);

    idx = 0;
    selections = new Array(quiz.length).fill(null);
    locked = new Array(quiz.length).fill(false);
    startedAt = null;
    clearInterval(timerInterval);
    timerInterval = null;
    timerEl.textContent = "01:00:00";
    timerNote.style.display = "inline-block";

    // UI pills
    candNamePill.textContent = candidate.name;
    candEmailPill.textContent = candidate.email;
    rankPill.textContent = activeRank;
    qTotalEl.textContent = quiz.length;

    // Switch screens
    startScreen.classList.add("hidden");
    resultScreen.classList.add("hidden");
    quizScreen.classList.remove("hidden");

    renderQuestion();
    updateProgressUI();
}

function startTimerIfNeeded() {
    if (startedAt) return;
    startedAt = Date.now();
    timerNote.style.display = "none";

    timerInterval = setInterval(() => {
        const elapsed = Math.floor((Date.now() - startedAt) / 1000);
        const remaining = QUIZ_SECONDS - elapsed;
        timerEl.textContent = fmtHMS(remaining);
        if (remaining <= 0) {
            clearInterval(timerInterval);
            timerInterval = null;
            submitQuiz(true);
        }
    }, 250);
}


function updateProgressUI() {
    const total = quiz.length;
    const answered = selections.filter(v => v !== null).length;
    const current = idx + 1;

    const pctAnswered = total ? Math.round((answered / total) * 100) : 0;
    const pctPosition = total ? Math.round((current / total) * 100) : 0;

    const pLeft = document.getElementById("progressLabelLeft");
    const pRight = document.getElementById("progressLabelRight");
    const bar = document.getElementById("progressBar");

    if (pLeft) pLeft.textContent = `Progress: ${answered}/${total} answered (${pctAnswered}%)`;
    if (pRight) pRight.textContent = `Question: ${current}/${total}`;
    if (bar) bar.style.width = `${pctPosition}%`;
}

function renderQuestion() {
    const q = quiz[idx];
    qIndexEl.textContent = (idx + 1);

    questionTextEl.textContent = q.q;
    optionsAreaEl.innerHTML = "";

    const selected = selections[idx];
    const isLocked = locked[idx];

    // Buttons
    nextBtn.disabled = (selected === null);
    const last = (idx === quiz.length - 1);
    submitBtn.classList.remove("hidden");
    nextBtn.classList.toggle("hidden", last);

    progressHint.textContent = (selected === null)
        ? "Select an option to enable Next (or Submit anytime)."
        : "Next is enabled.";

    q.options.forEach((opt, i) => {
        const id = `q${idx}_opt${i}`;
        const label = document.createElement("label");
        label.className = "option" + (isLocked ? " locked" : "");
        label.setAttribute("for", id);

        const radio = document.createElement("input");
        radio.type = "radio";
        radio.name = `q${idx}`;
        radio.id = id;
        radio.checked = (selected === i);
        radio.disabled = isLocked; // lock after first attempt

        radio.addEventListener("change", () => {
            if (locked[idx]) return;

            startTimerIfNeeded();

            selections[idx] = i;
            locked[idx] = true; // always lock after attempt

            applyInstantFeedback(i);
            updateProgressUI();
            // Enable next/submit
            nextBtn.disabled = false;
            if (idx === quiz.length - 1) {
                submitBtn.classList.remove("hidden");
                nextBtn.classList.add("hidden");
            }
        });

        const span = document.createElement("span");
        span.textContent = opt;

        label.appendChild(radio);
        label.appendChild(span);
        optionsAreaEl.appendChild(label);
    });

    // If already attempted, paint feedback
    if (isLocked && selected !== null) {
        applyInstantFeedback(selected, true);
    }

    // Update progress UI
    updateProgressUI();
}

function applyInstantFeedback(chosenIndex, alreadyLocked = false) {
    const q = quiz[idx];
    const correctIndex = q.answerIndex;
    const labels = optionsAreaEl.querySelectorAll("label.option");

    labels.forEach((lab, i) => {
        lab.classList.remove("correct", "wrongSelected");
        // show correct in green always after attempt
        if (i === correctIndex) {
            lab.classList.add("correct");
        }
        // if wrong selected, mark it red
        if (chosenIndex !== correctIndex && i === chosenIndex) {
            lab.classList.add("wrongSelected");
        }
    });

    // lock all radios after attempt
    const radios = optionsAreaEl.querySelectorAll('input[type="radio"]');
    radios.forEach(r => r.disabled = true);

    progressHint.textContent = "Answer locked. Feedback shown (Green/Red).";
}

function nextQuestion() {
    if (selections[idx] === null) return;
    if (idx < quiz.length - 1) {
        idx++;
        renderQuestion();
        updateProgressUI();
    }
}

function calcOverallScore() {
    let score = 0;
    for (let i = 0; i < quiz.length; i++) {
        if (selections[i] === quiz[i].answerIndex) score++;
    }
    return score;
}

function calcSectionScores() {
    // returns {sectionName: {correct, total, pct}}
    const map = {};
    for (let i = 0; i < quiz.length; i++) {
        const sec = quiz[i].section || "General";
        if (!map[sec]) map[sec] = { correct: 0, total: 0, pct: 0 };
        map[sec].total++;
        if (selections[i] === quiz[i].answerIndex) map[sec].correct++;
    }
    Object.keys(map).forEach(sec => {
        map[sec].pct = map[sec].total ? Math.round((map[sec].correct / map[sec].total) * 100) : 0;
    });
    return map;
}

function submitQuiz(isAuto = false) {
    clearInterval(timerInterval);
    timerInterval = null;

    // compute
    const score = calcOverallScore();
    const pct = Math.round((score / quiz.length) * 100);
    const sections = calcSectionScores();

    // pass/fail rules
    const overallPass = pct >= 70;
    const finalPass = overallPass;

    // fill result
    rName.textContent = candidate.name;
    rEmail.textContent = candidate.email;
    rRank.textContent = activeRank;
    rDate.textContent = new Date().toLocaleString();

    finalScoreEl.textContent = score;
    finalPctEl.textContent = pct;
    finalResultText.textContent = finalPass ? "PASS" : "FAIL";
    finalResultText.className = finalPass ? "ok" : "bad";

    // section-wise table
    const sectionRows = Object.keys(sections)
        .sort()
        .map(sec => {
            return `<tr>
          <td>${escapeHtml(sec)}</td>
          <td>${sections[sec].correct} / ${sections[sec].total}</td>
          <td>${sections[sec].pct}%</td>
        </tr>`;
        }).join("");

    sectionScoreWrap.innerHTML = `
      <div style="margin-top:14px;">
        <h3 style="margin:0 0 8px;">Section-wise Score</h3>
        <table class="sectionTable">
          <thead><tr><th>Section</th><th>Correct / Total</th><th>%</th></tr></thead>
          <tbody>${sectionRows}</tbody>
        </table>
      </div>
      <div class="muted" style="margin-top:10px;">
        ${isAuto
            ? "Note: The assessment was auto-submitted when the timer reached 00:00."
            : ""
        }
      </div>
    `;

    // review list
    reviewAreaEl.innerHTML = "";
    quiz.forEach((q, i) => {
        const your = selections[i];
        const correct = q.answerIndex;

        const yourText = (your === null) ? "Not answered" : q.options[your];
        const correctText = q.options[correct];
        const ok = (your === correct);

        const div = document.createElement("div");
        div.className = "reviewItem";
        div.innerHTML = `
        <div><strong>Q${i + 1}.</strong> ${escapeHtml(q.q)} <span class="muted">[${escapeHtml(q.section || "General")}]</span></div>
        <div class="muted" style="margin-top:6px;">
          <span class="${ok ? "ok" : "bad"}">${ok ? "Correct" : "Wrong"}</span><br>
          <strong>Your:</strong> ${escapeHtml(yourText)}<br>
          <strong>Correct:</strong> ${escapeHtml(correctText)}
        </div>
      `;
        reviewAreaEl.appendChild(div);
    });

    // switch screens
    quizScreen.classList.add("hidden");
    startScreen.classList.add("hidden");
    resultScreen.classList.remove("hidden");

    // --- Send results to backend ---
    sendScoreToBackend(sections);
}

async function sendScoreToBackend(sections) {
    // Build payload: email + rank + one key per section
    const payload = {
        email: candidate.email,
        rank: activeRank
    };
    Object.keys(sections).forEach(secName => {
        payload[secName] = {
            total_questions: sections[secName].total,
            correct_answers: sections[secName].correct
        };
    });

    // Show a status pill in the result screen while sending
    const statusPill = document.createElement("div");
    statusPill.className = "pill muted";
    statusPill.style.margin = "8px 0";
    statusPill.textContent = "Submitting results to serverâ¦";
    sectionScoreWrap.appendChild(statusPill);

    try {
        const resp = await fetch(`${BACKEND_URL}/create_score`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(payload)
        });
        const data = await resp.json();

        if (data.ok) {
            statusPill.textContent = "Results saved successfully.";
            statusPill.className = "pill";
            statusPill.style.color = "var(--ok)";
        } else {
            statusPill.textContent = "Server note: " + (data.message || "Could not save results.");
            statusPill.style.color = "var(--bad)";
        }
    } catch (e) {
        statusPill.textContent = "Could not reach server â results not saved remotely.";
        statusPill.style.color = "var(--bad)";
    }
}

function resetAll() {
    clearInterval(timerInterval);
    timerInterval = null;
    startedAt = null;
    idx = 0;
    selections = [];
    locked = [];
    quiz = [];

    // back to start
    resultScreen.classList.add("hidden");
    quizScreen.classList.add("hidden");
    startScreen.classList.remove("hidden");

    startHint.textContent = "Fill name + email, then start.";
}

