// =========================
// Study Guide
// =========================

const SECTION_ORDER = [
    "CORE COMPETENCIES",
    "REGULATIONS & COMPLIANCE",
    "OPERATIONS & SAFETY",
    "TECHNICAL KNOWLEDGE",
    "NAVIGATION & SEAMANSHIP",
    "EMERGENCY PROCEDURES",
    "LEADERSHIP & MANAGEMENT",
    "Deck Ratings",
];

const studyScreen   = document.getElementById("studyScreen");
const sgBackBtn     = document.getElementById("sgBackBtn");
const sgRankSelect  = document.getElementById("sgRankSelect");
const sgBody        = document.getElementById("sgBody");
const sgStatsEl     = document.getElementById("sgStats");
const sgExpandAllBtn   = document.getElementById("sgExpandAllBtn");
const sgCollapseAllBtn = document.getElementById("sgCollapseAllBtn");
const studyBtn      = document.getElementById("studyBtn");

const LETTERS = ["A", "B", "C", "D"];

function openStudyGuide() {
    // Sync rank selector with main start screen
    sgRankSelect.value = rankSelect.value;
    startScreen.classList.add("hidden");
    resultScreen.classList.add("hidden");
    quizScreen.classList.add("hidden");
    studyScreen.classList.add("active");
    renderStudyGuide(sgRankSelect.value);
}

function closeStudyGuide() {
    studyScreen.classList.remove("active");
    startScreen.classList.remove("hidden");
}

function renderStudyGuide(rank) {
    const allQ = BANKS[rank] || [];
    sgBody.innerHTML = "";
    sgStatsEl.innerHTML = "";

    if (!allQ.length) {
        sgBody.innerHTML = "<p style='color:#888;padding:14px'>No questions found for this rank.</p>";
        return;
    }

    // Group by section preserving order
    const grouped = {};
    allQ.forEach(q => {
        if (!grouped[q.section]) grouped[q.section] = [];
        grouped[q.section].push(q);
    });

    const orderedSections = SECTION_ORDER.filter(s => grouped[s])
        .concat(Object.keys(grouped).filter(s => !SECTION_ORDER.includes(s)));

    // Stats bar
    sgStatsEl.innerHTML =
        `<div class="sgStat">Rank: <strong>${escapeHtml(rank)}</strong></div>` +
        `<div class="sgStat">Total Questions: <strong>${allQ.length}</strong></div>` +
        `<div class="sgStat">Sections: <strong>${orderedSections.length}</strong></div>`;

    // Build section blocks
    orderedSections.forEach((secName, si) => {
        const qs = grouped[secName];
        const block = document.createElement("div");
        block.className = "sgSectionBlock";
        block.dataset.section = si;

        const head = document.createElement("div");
        head.className = "sgSectionHead";
        head.innerHTML =
            `<span class="sgSectionTitle">Section ${si + 1}: ${escapeHtml(secName)}</span>` +
            `<span class="sgSectionMeta">${qs.length} questions</span>` +
            `<span class="sgChevron">â¼</span>`;
        head.addEventListener("click", () => block.classList.toggle("open"));

        const list = document.createElement("div");
        list.className = "sgQList";

        qs.forEach((q, qi) => {
            const item = document.createElement("div");
            item.className = "sgQItem";

            let optsHtml = "";
            q.options.forEach((opt, oi) => {
                const isCorrect = oi === q.answerIndex;
                const cls = isCorrect ? "sgOpt correct" : "sgOpt wrong";
                const badge = isCorrect ? `<span class="sgCorrectBadge">Correct</span>` : "";
                optsHtml += `<div class="${cls}">
                    <span class="sgOptLetter">${LETTERS[oi]}.</span>
                    <span>${escapeHtml(opt)}</span>
                    ${badge}
                  </div>`;
            });

            item.innerHTML =
                `<div class="sgQNum">Q${q.id}</div>` +
                `<div class="sgQText">${escapeHtml(q.q)}</div>` +
                `<div class="sgOptions">${optsHtml}</div>`;

            list.appendChild(item);
        });

        block.appendChild(head);
        block.appendChild(list);
        sgBody.appendChild(block);
    });
}

studyBtn.addEventListener("click", openStudyGuide);
sgBackBtn.addEventListener("click", closeStudyGuide);
sgRankSelect.addEventListener("change", () => renderStudyGuide(sgRankSelect.value));

sgExpandAllBtn.addEventListener("click", () => {
    sgBody.querySelectorAll(".sgSectionBlock").forEach(b => b.classList.add("open"));
});
sgCollapseAllBtn.addEventListener("click", () => {
    sgBody.querySelectorAll(".sgSectionBlock").forEach(b => b.classList.remove("open"));
});

